mapper.select:
select id, username, password, id_number, status, email, phone, tid,`name`
from user
where 1=1

    and username like CONCAT('%',#{username},'%')


    and id_number = #{id_number}


    and email = #{email}


    and phone = #{phone}


    and status = #{status}


    and tid = #{tid}


    and `name` like CONCAT('%',#{name},'%')

select id, username, password, id_number, status, email, phone, tid,`name`
from user
where 1=1

    and username = #{username}


    and id_number = #{id_number}


    and email = #{email}


    and phone = #{phone}


    and status = #{status}


    and tid = #{status}

mapper.update:
update `user`
set tid = #{tid}
where id in
#{sids}
and status = 1

update `user`
set tid = null
where id in
#{sids}
and status = 1
and tid = #{tid}
